import React from 'react'
import { useDispatch, useSelector } from 'react-redux';

function Contact() {

    // constructor(props) {
    //     super(props);
    //     this.state = {
    //         nama : '',
    //         email : '',
    //         pesan : '',
    //     }

    //     this.onSubmit = this.onSubmit.bind(this);
    //     this.inputNama =  React.createRef();
    //     this.inputEmail = React.createRef();
    //     this.inputPesan = React.createRef();
        
    // };

    // onSubmit(event){
    //     event.preventDefault();
    //     event.stopPropagation();
    //     // console.log(
    //     //     `Email : ${this.inputEmail.current.value}
    //     //     ${this.inputNama.current.value }
    //     //     ${this.inputPesan.current.value} `            
    //     // );

    //     this.setState({[this.nama] : this.inputNama.current.value});
    // }

    // render() {

        // const {nama, email, pesan } = this.state;
        let pesanRef;
        const dispatch = useDispatch();
        const pesan = useSelector(state => state.pesan);
        
        const _handlePesan = (e) => {
            let newPesan = pesanRef.value;
            dispatch({
                type : 'ADD PESAN',
                pesan : newPesan
            })

            e.preventDefault();
            pesanRef.value = '';
        }

        return(
            <div className="App">
                <header className="App-header">                
                    
                    Kirimkan pesan <br />
                    <form onSubmit={_handlePesan}>
                        <table>
                            <thead></thead>
                            <tbody>
                            {/* <tr>
                                <th>Email</th>
                                <td><input type="email" size="100" placeholder="input your email" ref={this.inputEmail}></input></td>
                            </tr>
                            <tr>
                                <th>Nama</th>
                                <td><input type="text" size="100" placeholder="input your name" ref={this.inputNama}></input></td>
                            </tr> */}
                            <tr>
                                <th>Pesan</th>
                                <td><input name="pesan" rows="4" cols="75" ref={input => pesanRef = input}></input></td>
                            </tr>
                            <tr>
                                <td colSpan="2">
                                    <input type="submit" className="btn-pesan" value="Kirim"></input>
                                </td>
                            </tr>
                            </tbody>
                        </table>
                    </form>
                <div>
                    <ul> 
                        {pesan.map((item, index) => <li className="ul-pesan" key={index}>{item}</li> )}
                    </ul>
                </div>
                    
                </header>
            </div>
        );
    }
// }

export default Contact;
