import React from 'react'
import { useDispatch, useSelector } from 'react-redux';

function Contact() {

        let pesanRef;
        let nameRef;
        let emailRef;
        const dispatch = useDispatch();
        const pesan = useSelector(state => state.pesan);
        
        const _handlePesan = (e) => {
            let newPesan = pesanRef.value;
            let newEmail = emailRef.value;
            let newName = nameRef.value;
            dispatch({
                type : 'ADD PESAN',
                pesan : newPesan
            })

            e.preventDefault();
            pesanRef.value = '';
            emailRef.value = '';
            nameRef.value = '';
        }

        return(
            <div className="App">
                <header className="App-header">                
                    
                    Kirimkan pesan <br />
                    <form onSubmit={_handlePesan}>
                        <table>
                            <thead></thead>
                            <tbody>
                            <tr>
                                <th>Email</th>
                                <td><input type="email" size="100" placeholder="input your email" ref={input => emailRef = input}></input></td>
                            </tr>
                            <tr>
                                <th>Nama</th>
                                <td><input type="text" size="100" placeholder="input your name" ref={input => nameRef = input}></input></td>
                            </tr>
                            <tr>
                                <th>Pesan</th>
                                <td><input name="pesan" rows="4" cols="75" ref={input => pesanRef = input}></input></td>
                            </tr>
                            <tr>
                                <td colSpan="2">
                                    <input type="submit" className="btn-pesan" value="Kirim"></input>
                                </td>
                            </tr>
                            </tbody>
                        </table>
                    </form>
                <div>
                    <ol> 
                        {pesan.map((item, index) => <li className="ul-pesan" key={index}>{item}</li> )}
                    </ol>
                </div>                    
                </header>
            </div>
        );
    }

export default Contact;
