import React from 'react'

export default class Contact extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            nama : '',
            email : '',
            pesan : '',
        }

        this.onSubmit = this.onSubmit.bind(this);
        this.inputNama =  React.createRef();
        this.inputEmail = React.createRef();
        this.inputPesan = React.createRef();
    };

    onSubmit(event){
        event.preventDefault();
        event.stopPropagation();
        // console.log(
        //     `Email : ${this.inputEmail.current.value}
        //     ${this.inputNama.current.value }
        //     ${this.inputPesan.current.value} `            
        // );

        this.setState({[nama] : this.inputNama.current.value});
    }

    render() {

        const {nama, email, pesan } = this.state;

        return(
            <div className="App">
                <header className="App-header">                
                    
                    Kirimkan pesan <br />
                    <form onSubmit={this.onSubmit}>
                        <table>
                            <thead></thead>
                            <tbody>
                            <tr>
                                <th>Email</th>
                                <td><input type="email" size="100" placeholder="input your email" ref={this.inputEmail}></input></td>
                            </tr>
                            <tr>
                                <th>Nama</th>
                                <td><input type="text" size="100" placeholder="input your name" ref={this.inputNama}></input></td>
                            </tr>
                            <tr>
                                <th>Pesan</th>
                                <td><textarea name="pesan" rows="4" cols="75" ref={this.inputPesan}></textarea></td>
                            </tr>
                            <tr>
                                <td colSpan="2">
                                    <input type="submit" className="btn-pesan"></input>
                                </td>
                            </tr>
                            </tbody>
                        </table>
                    </form>
                <div>
                    <ul>
                        <li>Nama : {nama}</li>
                        <li>Email : {email} </li>
                        <li>Pesan : {pesan} </li>
                    </ul>
                </div>
                    
                </header>
            </div>
        )
    }
}
